INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `newday`, `recharge`, `partrecharge`, `mountfeedcost`, `mountlocation`, `mountdkcost`)
VALUES
	(1, 'Poni', 'Esta dócil bestia es joven todavía. Pero es muy versátil y ayudará a su amo en todo aquello para lo que está capacitada.', 'Caballos', 'a:6:{s:4:\"name\";s:21:\"`&Ataque montura Poni\";s:8:\"roundmsg\";s:24:\"¡Tu poni lucha contigo!\";s:7:\"wearoff\";s:41:\"¡Tu poni se ha cansado y deja de luchar!\";s:6:\"rounds\";s:2:\"20\";s:6:\"atkmod\";s:3:\"1.1\";s:6:\"schema\";s:6:\"mounts\";}', 6, 0, 1, 0, 'Pones tu `i{weapon}`i en las alforjas de tu poni, ¡y te lanzas en busca de aventuras!', '`&Recordando que ha pasado bastante tiempo desde la última vez que alimentaste a su poni, decides que es el momento perfecto para relajarse y dejar que paste en el campo un poco. Te duermes para disfrutar de la paz y tranquilidad.`0', '`&Desmontas en el campo para permitir a tu poni pastar un momento a pesar de haberle alimentado recientemente. Ya que se tumba en la hierba para mirar las nubes, su poni trota suavemente hacia la maleza. Busca durante un tiempo, esperando a que tu poni vuelva, un tiempo más tarde, su poni trota de vuelta sosteniendo su cabeza en alto, mirando mucho más lleno de energía y con una sonrisa muy equina en su cara.`0', 20, 'all', 0),
	(2, 'Caballo castrado', 'Esta poderosa bestia es ferozmente leal a su amo.', 'Caballos', 'a:6:{s:4:\"name\";s:36:\"`&Ataque de montura Caballo castrado\";s:8:\"roundmsg\";s:36:\"¡Tu caballo castrado ataca contigo!\";s:7:\"wearoff\";s:57:\"¡Tu caballo castrado de ha cansado de luchar junto a ti!\";s:6:\"rounds\";s:2:\"40\";s:6:\"atkmod\";s:4:\"1.15\";s:6:\"schema\";s:6:\"mounts\";}', 10, 0, 1, 0, 'Pones tu `i{weapon}`i en las alforjas de tu cabalo castrado, ¡y sales en busca de aventuras!', '`&Recordando que ha pasado bastante tiempo desde la última vez que diste de comer a tu caballo castrado, dedices que ha llegado el momento perfecto para relajarse y dejar que paste por el campo un poco. Te duermes con esta paz y tranquilidad.`0', '`&Desmontas en el campo para permitir a tu caballo castrado pastar un momento a pesar de haberle alimentado recientemente. Ya que se tumba en la hierba para mirar las nubes, su caballo trota suavemente hacia la maleza. Busca durante un tiempo, esperando a que tu caballo vuelva, un tiempo más tarde, su caballo trota de vuelta sosteniendo su cabeza en alto, mirando mucho más lleno de energía y con una sonrisa muy equina en su cara.`0', 25, 'all', 0),
	(3, 'Semental', '¡Esta noble bestia es enorme y poderosa!', 'Caballos', 'a:5:{s:4:\"name\";s:25:\"`&Ataque montura Semental\";s:8:\"roundmsg\";s:28:\"¡Tu semental lucha contigo!\";s:6:\"rounds\";s:2:\"60\";s:6:\"atkmod\";s:3:\"1.2\";s:6:\"schema\";s:6:\"mounts\";}', 16, 0, 1, 0, 'Pones tu `i{weapon}`i en las alforjas de tu semental, ¡y te lanzas en busca de aventuras!', '`&Recordando que ha pasado bastante tiempo desde la última vez que alimentaste a su semental, decides que es el momento perfecto para relajarse y dejar que paste en el campo un poco. Te duermes para disfrutar de la paz y tranquilidad.`0', '`&Desmontas en el campo para permitir a tu semental pastar un momento a pesar de haberle alimentado recientemente. Ya que se tumba en la hierba para mirar las nubes, su semental trota suavemente hacia la maleza. Busca durante un tiempo, esperando a que tu semental vuelva, un tiempo más tarde, su poni trota de vuelta sosteniendo su cabeza en alto, mirando mucho más lleno de energía y con una sonrisa muy equina en su cara.`0', 30, 'all', 0);
