	"INSERT INTO " . db_prefix("masters") . " VALUES (1,'Mireraband',1,'Small Dagger','Well done {goodguy}`&, I should have guessed you\\'d grown some.','As I thought, {goodguy}`^, your skills are no match for my own!',NULL,NULL,12,2,2)",
	"INSERT INTO " . db_prefix("masters") . " VALUES (2,'Fie',2,'Short Sword','Well done {goodguy}`&, you really know how to use your {weapon}.','You should have known you were no match for my {creatureweapon}',NULL,NULL,22,4,4)",
	"INSERT INTO " . db_prefix("masters") . " VALUES (3,'Glynyc',3,'Hugely Spiked Mace','Aah, defeated by the likes of you!  Next thing you know, Mireraband will be hunting me down!','Haha, maybe you should go back to Mireraband\\'s class.',NULL,NULL,33,6,6)",
	"INSERT INTO " . db_prefix("masters") . " VALUES (4,'Guth',4,'Spiked Club','Ha!  Hahaha, excellent fight {goodguy}`&!  Haven\\'t had a battle like that since I was in the guard!','Back there, we\\'d have eaten the likes of you alive!  Go work on your skills some old boy!',NULL,NULL,44,8,8)",
	"INSERT INTO " . db_prefix("masters") . " VALUES (5,'Un�lith',5,'Thought Control','Your mind is greater than mine.  I concede defeat.','Your mental powers are lacking.  Meditate on this failure and perhaps some day you will defeat me.',NULL,NULL,55,10,10)",
	"INSERT INTO " . db_prefix("masters") . " VALUES (6,'Adwares',6,'Dwarven Battle Axe','Ach!  Y\\' do hold yer {weapon} with skeel!','Har!  Y\\' do be needin moore praktise y\\' wee cub!',NULL,NULL,66,12,12)",
	"INSERT INTO " . db_prefix("masters") . " VALUES (7,'Gerrard',7,'Battle Bow','Hmm, mayhaps I underestimated you.','As I thought.',NULL,NULL,77,14,14)",
	"INSERT INTO " . db_prefix("masters") . " VALUES (8,'Ceiloth',8,'Orkos Broadsword','Well done {goodguy}`&, I can see that great things lie in the future for you!','You are becoming powerful, but not yet that powerful.',NULL,NULL,88,16,16)",
	"INSERT INTO " . db_prefix("masters") . " VALUES (9,'Dwiredan',9,'Twin Swords','Perhaps I should have considered your {weapon}...','Perhaps you\\'ll reconsider my twin swords before you try that again?',NULL,NULL,99,18,18)",
	"INSERT INTO " . db_prefix("masters") . " VALUES (10,'Sensei Noetha',10,'Martial Arts Skills','Your style was superior, your form greater.  I bow to you.','Learn to adapt your style, and you shal prevail.',NULL,NULL,110,20,20)",
	"INSERT INTO " . db_prefix("masters") . " VALUES (11,'Celith',11,'Throwing Halos','Wow, how did you dodge all those halos?','Watch out for that last halo, it\\'s coming back this way!',NULL,NULL,121,22,22)",
	"INSERT INTO " . db_prefix("masters") . " VALUES (12,'Gadriel the Elven Ranger',12,'Elven Long Bow','I can accept that you defeated me, because after all elves are immortal while you are not, so the victory will be mine.','Do not forget that elves are immortal.  Mortals will likely never defeat one of the fey.',NULL,NULL,132,24,24)",
	"INSERT INTO " . db_prefix("masters") . " VALUES (13,'Adoawyr',13,'Gargantuan Broad Sword','If I could have picked up this sword, I probably would have done better!','Haha, I couldn\\'t even pick the sword UP and I still won!',NULL,NULL,143,26,26)",
	"INSERT INTO " . db_prefix("masters") . " VALUES (14,'Yoresh',14,'Death Touch','Well, you evaded my touch.  I salute you!','Watch out for my touch next time!',NULL,NULL,154,28,28)",

