INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `newday`, `recharge`, `partrecharge`, `mountfeedcost`, `mountlocation`, `mountdkcost`) VALUES
	(1, 'Pony', 'This docile beast is young yet.', 'Horses', 'a:25:{s:4:"name";s:13:"`&Pony Attack";s:8:"roundmsg";s:26:"Your pony fights with you!";s:6:"rounds";d:20;s:6:"atkmod";d:1.2;s:8:"activate";s:7:"offense";s:7:"wearoff";N;s:9:"effectmsg";N;s:14:"effectnodmgmsg";N;s:6:"defmod";N;s:5:"regen";N;s:11:"minioncount";N;s:15:"minbadguydamage";N;s:15:"maxbadguydamage";N;s:16:"mingoodguydamage";N;s:16:"maxgoodguydamage";N;s:7:"lifetap";N;s:12:"damageshield";N;s:12:"badguydmgmod";N;s:12:"badguyatkmod";N;s:12:"badguydefmod";N;s:10:"allowinpvp";b:0;s:12:"allowintrain";b:0;s:13:"survivenewday";b:0;s:16:"expireafterfight";b:0;s:12:"invulnerable";b:0;}', 6, 0, 1, 1, 'You strap your {weapon} to your pony\'s saddle bags, and head out for some adventure!', '`&Remembering that is has been quite some time since you last fed your pony, you decide this is a perfect time to relax and allow it to graze the field a bit. You doze off enjoying this peaceful serenity.`0', '`&You dismount in the field to allow your pony to graze for a moment even though it has recently been fully fed.  As you lean back in the grass to watch the clouds, your pony whickers softly and trots off into the underbrush.  You search for a while before returning to the fields hoping that it\'ll return.  A short time later, your pony trots back into the clearing holding its head high, looking much more energized and with a very equine grin on its face.`0', 20, 'all', 0),
	(2, 'Gelding', 'This powerful beast is fiercely loyal.', 'Horses', 'a:25:{s:4:"name";s:16:"`&Gelding Attack";s:8:"roundmsg";s:29:"Your gelding fights with you!";s:6:"rounds";i:40;s:6:"atkmod";d:1.2;s:8:"activate";s:7:"offense";s:7:"wearoff";N;s:9:"effectmsg";N;s:14:"effectnodmgmsg";N;s:6:"defmod";N;s:5:"regen";N;s:11:"minioncount";N;s:15:"minbadguydamage";N;s:15:"maxbadguydamage";N;s:16:"mingoodguydamage";N;s:16:"maxgoodguydamage";N;s:7:"lifetap";N;s:12:"damageshield";N;s:12:"badguydmgmod";N;s:12:"badguyatkmod";N;s:12:"badguydefmod";N;s:10:"allowinpvp";b:0;s:12:"allowintrain";b:0;s:13:"survivenewday";b:0;s:16:"expireafterfight";b:0;s:12:"invulnerable";b:0;}', 10, 0, 1, 2, 'You strap your {weapon} to your gelding\'s saddle bags, and head out for some adventure!', '`&Remembering that is has been quite some time since you last fed your gelding, you decide this is a perfect time to relax and allow it to graze the field a bit. You doze off enjoying this peaceful serenity.`0', '`&You dismount in the field to allow your gelding to graze for a moment even though it has recently been fully fed.  As you lean back in the grass to watch the clouds, your gelding whickers softly and trots off into the underbrush.  You search for a while before returning to the fields hoping that it\'ll return.  A short time later, your gelding trots back into the clearing holding its head high, looking much more energized and with a very equine grin on its face.`n`nAnd here you thought geldings weren\'t equipped that way any longer!`0', 25, 'all', 0),
	(3, 'Stallion', 'This noble beast is huge and powerful!', 'Horses', 'a:25:{s:4:"name";s:17:"`&Stallion Attack";s:8:"roundmsg";s:30:"Your stallion fights with you!";s:6:"rounds";i:60;s:6:"atkmod";d:1.2;s:8:"activate";s:7:"offense";s:7:"wearoff";N;s:9:"effectmsg";N;s:14:"effectnodmgmsg";N;s:6:"defmod";N;s:5:"regen";N;s:11:"minioncount";N;s:15:"minbadguydamage";N;s:15:"maxbadguydamage";N;s:16:"mingoodguydamage";N;s:16:"maxgoodguydamage";N;s:7:"lifetap";N;s:12:"damageshield";N;s:12:"badguydmgmod";N;s:12:"badguyatkmod";N;s:12:"badguydefmod";N;s:10:"allowinpvp";b:0;s:12:"allowintrain";b:0;s:13:"survivenewday";b:0;s:16:"expireafterfight";b:0;s:12:"invulnerable";b:0;}', 16, 0, 1, 3, 'You strap your {weapon} to your stallion\'s saddle bags, and head out for some adventure!', '`&Remembering that is has been quite some time since you last fed your stallion, you decide this is a perfect time to relax and allow it to graze the field a bit. You doze off enjoying this peaceful serenity.`0', '`&You dismount in the field to allow your stallion to graze for a moment even though it has recently been fully fed.  As you lean back in the grass to watch the clouds, your stallion whickers softly and trots off into the underbrush.  You search for a while before returning to the fields hoping that it\'ll return.  A short time later, your stallion trots back into the clearing holding its head high, looking much more energized and with a very equine grin on its face.`0', 30, 'all', 0);
